mapper.select:
		select * from tms_test
		
			
				tms_test_reps_id=#{tmsTestRepsId}
			
			
				and tx_id=#{txId}
			

			
				and tigan like concat('%',#{tiGan},'%')
			
		
		order by utime

select count(id) from tms_test

	
		tms_test_reps_id=#{tmsTestId}
	
	
		and tx_id=#{txId}
	
	
		and tigan like concat('%',#{tiGan},'%')
