mapper.select:
select * from tms_test_package


		tms_kind_type_id=#{tmsKindTypeId}


		and tms_course_type_id=#{tmsCourseTypeId}


		and title like concat('%',#{title},'%')




	limit #{from} ,#{limit}

select count(id) from tms_test_pager


		tms_kind_type_id=#{tmsKindTypeId}


		and tms_course_type_id=#{tmsCourseTypeId}


		and title like concat('%',#{title},'%')
